document.getElementById("ageClick").addEventListener("click", askYear);
var print = document.getElementById("result");

function calculateAge(yearOfBirth) {
  var currentYear = new Date().getFullYear();
  var age = currentYear - yearOfBirth;
  return age;
}

function askYear(event) {
  event.preventDefault()
  var calculate = document.getElementById("inputYear").value; 
  if (calculate === "") {
    alert("Please fill the content");
  } else {
    hide();
    var result = calculateAge(calculate);
    var resultado =
      `<h1>You are only <br>
            <span class= "rounded-left" >` +
      result +
      `</span> years old... 
        </h1>
          <h2>Too young Enjoy life <i class="far fa-smile"></i></h2> 
          <a  href="index.html" class="btn btn-outline btn-xl">Again</a>
          `;
    print.innerHTML += resultado;
    resultado = "";
  }
  return resultado;
}

function hide() {
  document.getElementById("headerContent").style.display = "none";
}