document.getElementById("ageClick").addEventListener("click", askYear);
var print = document.getElementById("result");
var resultado;

/*Text in base of age*/

function calculateAge(yearOfBirth) {
  var currentYear = new Date().getFullYear();
  var age = currentYear - yearOfBirth;
  return age;
}

function askYear(event) {
  event.preventDefault();
  if (calculate === "") {
    alert("Please fill the content");
  } else {
    hide();
    var calculate = document.getElementById("inputYear").value;
    var result = calculateAge(calculate);
    var infant = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2>Omg you are an Infant <i class="far fa-smile"></i></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;
    var child = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2> Cool, you have plenty time to fun<i class="far fa-smile"></i></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;
    var adolescent = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2> TBT (When it isn't Thursday)<i class="far fa-smile"></i></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;
    var adult = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2> OMG you are Old <i class="far fa-smile"></i></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;

    if (result <= 1) {
      resultado = infant;
    } else if (result > 1 & result <= 10) {
      resultado = child;
    } else if (result > 10 && result <= 18) {
      resultado = adolescent;
    } else {
      resultado = adult;
    }
    print.innerHTML += resultado;
    resultado = "";
  }
}

function hide() {
  document.getElementById("headerContent").style.display = "none";
}