    var result = calculateAge(calculate);
    var unborn = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2>Omg you are a spermatozoid <img src="img/fertility.png" class="img-fluid" alt=""></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;
