    var child = `<h1> You are only <br> <span class= "rounded-left" >` + result + `</span> years old... </h1> <h2> Cool, you have plenty time to play<img src="img/kids-couple.png" class="img-fluid" alt=""></h2><a  href="index.html" class="btn btn-outline btn-xl">Again</a>`;
