/*Text in base of age*/

function calculateAge(yearOfBirth) {
  var currentYear = new Date().getFullYear();
  var age = currentYear - yearOfBirth;
  return age;
}

function askYear(event) {
  event.preventDefault();
  var resultado;
  if (calculate === "") {
    alert("Please fill the content");
  } else {
    hide();
    var calculate = document.getElementById("inputYear").value;
    var result = calculateAge(calculate);
    var unborn = youAre + result + yearsOld + textArray[0] + backButton;
    var infant = youAre + result + yearsOld + textArray[1] + backButton;
    var child = youAre + result + yearsOld + textArray[2] + backButton;
    var adolescent = youAre + result + yearsOld + textArray[3] + backButton;
    var adult = youAre + result + yearsOld + textArray[4] + backButton;

    if (result > 0 && result === 1) {
      resultado = infant;
    } else if (result > 1 & result <= 10) {
      resultado = child;
    } else if (result > 10 && result <= 18) {
      resultado = adolescent;
    } else if (result <= 0) {
      resultado = unborn;
    } else {
      resultado = adult;
    }
    print.innerHTML += resultado;
    resultado = "";
  }
}

function hide() {
  document.getElementById("headerContent").style.display = "none";
}